import React, { useState } from "react";

import "./SquareTable.css";

const SquareTable = (props) => {
  let inputFromGamePrp = props.inputFromGame;
  const [newInput,setNewInput] = useState("X");
  const onUserClick = (event) => {
    console.log(inputFromGamePrp + "value");
    if (inputFromGamePrp === "X") {
      newInput = "O";
      setNewInput("O");
    } else {
        setNewInput("X");
      newInput = "";
    }
  };
  return (
    <table>
      <tr>
        <td>
          <button onClick={onUserClick}>{newInput}</button>
        </td>
        <td>
          <button onClick={onUserClick}>{newInput}</button>
        </td>
        <td>
          <button onClick={onUserClick}>{newInput}</button>
        </td>
      </tr>
    </table>
  );
};
export default SquareTable;
