import React, { useState } from "react";

import "./SquareTable.css";

const SquareTable = (props) => {
  let newClassName = "";
  const [newInput, setNewInput] = useState("S");

  const onUserClick = (event) => {
    if (newInput === "X") {
      newClassName = inputO;
      setNewInput("O");
    } else {
      newClassName = inputX;
      setNewInput("X");
    }
  };
  return (
    <td>
      <button
        className={newClassName}
        onClick={onUserClick}
      >
        {newInput}
      </button>
    </td>
  );
};
export default SquareTable;
