import React, { useState } from "react";

import "./SquareTable.css";

const SquareTable = (props) => {
  let newClassName = "";
  const [newInput, setNewInput] = useState("S");

  const onUserClick = (event) => {
    if (newInput === "X") {
      setNewInput("O");
    } else {
      setNewInput("X");
    }
  };
  return (
    // className={`form-control ${!isValid ? "invalid" : ""}`}
    <td>
      <button
        className={`${newInput === "X" ? "inputX" : "inputO"}`}
        onClick={onUserClick}
      >
        {newInput}
      </button>
    </td>
  );
};
export default SquareTable;
