import React, { useState } from "react";

import "./SquareTable.css";

const SquareTable = (props) => {
  const [newInput, setNewInput] = useState("S");
  let userScore = Number(props.dataValue);
  let newScore = 0;
  const onUserClick = (event) => {
    if (newInput === "X") {
    //   checkUserScore(userScore);
      newScore += userScore;
      setNewInput(`O ${newScore}`);
    } else {
        newScore +=userScore;
        setNewInput(`O ${newScore}`);
    //   checkUserScore(props.dataValue);
    //   setNewInput("X");
    }
  };
  const checkUserScore = (Userscore) => {
    const winText = "Win!";
    switch ((Userscore += props.dataValue)) {
      case 460:
        return winText;
      case 529:
        return winText;
      case 88:
        return winText;
      case 122:
        return winText;
      case 18:
        return winText;
      case 823:
        return winText;
      case 515:
        return winText;
      case 292:
        return winText;
      default:
        return "tried";
    }
  };
  return (
    <td>
      <button
        className={`${
          newInput === "S" ? "" : newInput === "X" ? "inputX" : "inputO"
        }`}
        onClick={onUserClick}
      >
        {newInput + userScore}
      </button>
    </td>
  );
};
export default SquareTable;
